import React, { Component } from "react";
import Select from "react-select";
import axios from "axios";
import AdminPanelService from "../../service/AdminPanelService";
import EgyesuletekService from "../../service/EgyesuletekService";

class AdminComponent extends Component {
  constructor(props) {
    super(props);

    this.state = {
      id: this.props.match.params.id,
      nev: "",
      helyseg: "",
      elnok: "",
    };
    console.log(this.state.selectOptions);
  }
  componentDidMount() {
    console.log(this.state.id);
    if (this.state.id === "_add") {
      return;
    } else {
      EgyesuletekService.getEgyesuletById(this.state.id).then((res) => {
        let egyesulet = res.data;
        console.log(res.data);
        this.setState({
          id: res.data.id,
          nev: res.data.nev,
          helyseg: res.data.helyseg,
          elnok: res.data.elnok,
        });
        console.log(egyesulet);
      });
    }
    console.log(this.state);
  }

  saveOrUpdateEgyesulet = (e) => {
    e.preventDefault();
    let egyeUpdate = {
      id: this.state.id,
      nev: this.state.nev,
      cim: this.state.cim,
      elnok: this.state.elnok,
      biro: this.state.biro,
      egyesuletId: this.state.egyesuletId,
    };
    let egyeAdd = {
      nev: this.state.nev,
      cim: this.state.cim,
      elnok: this.state.elnok,
      biro: this.state.biro,
      egyesuletId: this.state.egyesuletId,
    };
    console.log("egye => " + JSON.stringify(egyeAdd));
    console.log("egye => " + JSON.stringify(egyeUpdate));

    if (this.state.id === "_add") {
      TenyesztokService.addTenyeszto(egyeAdd).then((res) => {});
    } else {
      TenyesztokService.updateTenyeszto(
        egyeUpdate,
        this.state.id
      ).then((res) => {});
    }
  };

  changeNevHandler = (event) => {
    this.setState({ nev: event.target.value });
  };

  changeCimHandler = (event) => {
    this.setState({ cim: event.target.value });
  };
  changeTelHandler = (event) => {
    this.setState({ elnok: event.target.value });
  };
  changeBiroHandler = (event) => {
    this.setState({ biro: event.target.value });
    console.log(event.target.value);
  };
  changeEgyesuletHandler = (event) => {
    this.setState({ egyesuletId: event.target.value });
    console.log(event.target.value);
  };

  
  render() {
    if (!this.state.selectOptions.length) return null;

    let egyesuletek = this.state.selectOptions;
    const arbitru = [
      { value: "true", label: "Da" },
      { value: "false", label: "Nu" },
    ];

    return (
      <div>
        <form class="addForm">
          <div>
            <label> Nume: </label>
            <input
              placeholder="Nume"
              name="nume"
              className="form-control"
              value={this.state.nev}
              onChange={this.changeNevHandler}
            />
          </div>
          <div>
            <label> Adresa: </label>
            <input
              placeholder="Adresa"
              name="adresa"
              className="form-control"
              value={this.state.cim}
              onChange={this.changeCimHandler}
            />
          </div>
          <div>
            <label> Nr tel: </label>
            <input
              placeholder="Nr tel"
              name="telefon"
              className="form-control"
              value={this.state.elnok}
              onChange={this.changeTelHandler}
            />
          </div>
          <div>
            <label> Arbitru: </label>
            <select
              placeholder={this.state.biro == 0 ? "nu" : "da"}
              onChange={this.changeBiroHandler}
            >
              {arbitru.map((option) => (
                <option value={option.value}>{option.label}</option>
              ))}
            </select>
          </div>

          <div>
            <label> Asociatie: </label>
            <select
              value={this.state.selectOptions.value}
              onChange={this.changeEgyesuletHandler}
            >
              {this.state.selectOptions.map((option) => (
                <option value={option.value}>{option.label}</option>
              ))}
            </select>
          </div>
          <button className="btn btn-success" onClick={this.saveOrUpdateegye}>
            Save
          </button>
        </form>
      </div>
    );
  }
}

export default AdminComponent;
