import React, { Component } from "react";
import Select from "react-select";
import axios from "axios";
import AdminPanelService from "../../service/AdminPanelService";
import TenyesztokService from "../../service/TenyesztokService";

class AdminComponent extends Component {
  constructor(props) {
    super(props);

    this.state = {
      id: this.props.match.params.id,
      nev: "",
      cim: "",
      telszam: "",
      biro: "",
      email: "",
      egyesulet: "",
    };
  }

  componentDidMount() {
    if (this.state.id === "add") {
      return;
    } else {
      // TenyesztokService.getTenyesztoById(this.state.id).then((res) => {
      //   let tulaj = res.data;
      //   this.setState({
      //     nume: tulaj.nev,
      //     cim: tulaj.cim,
      //     telszam: tulaj.telszam,
      //     biro: tulaj.biro,
      //     email: tulaj.email,
      //     egyesulet: tulaj.egyesulet,
      //   });
      
    }
  }

  saveOrUpdateTulaj = (e) => {
    e.preventDefault();
    let tulaj = {
      nev: this.state.nev,
      cim: this.state.cim,
      telszam: this.state.telszam,
      biro: this.state.biro,
      email: this.state.email,
      egyesulet: this.state.email,
    };
    console.log("tulaj => " + JSON.stringify(tulaj));

    if (this.state.id === "add") {
      TenyesztokService.addTenyeszto(tulaj).then((res) => {});
    } else {
      TenyesztokService.updateTenyeszto(tulaj, this.state.id).then((res) => {});
    }
  };

  changeFirstNameHandler = (event) => {
    this.setState({ firstName: event.target.value });
  };

  changeLastNameHandler = (event) => {
    this.setState({ lastName: event.target.value });
  };

  changeEmailHandler = (event) => {
    this.setState({ emailId: event.target.value });
  };

  async getOptions() {
    const res = await axios.get("http://localhost:8090/api/egyesulet");
    const data = res.data;

    const options = data.map((response) => ({
      value: response.id,
      label: response.nev,
    }));
    this.setState({ selectOptions: options });
    console.log(options);
  }
  componentDidMount() {
    this.getOptions();
  }
  render() {
    const arbitru = [
      { value: "da", label: "Da" },
      { value: "nu", label: "Nu" },
    ];
    return (
      <div>
        <form class="addForm">
          <div>
            <label> Nume: </label>
            <input
              placeholder={this.state.tenyeszto.nev}
              name="nume"
              className="form-control"
              value={this.state.firstName}
              onChange={this.changeFirstNameHandler}
            />
          </div>
          <div>
            <label> Adresa: </label>
            <input
              placeholder="Adresa"
              name="adresa"
              className="form-control"
              value={this.state.lastName}
              onChange={this.changeLastNameHandler}
            />
          </div>
          <div>
            <label> Nr tel: </label>
            <input
              placeholder="Telefon"
              name="telefon"
              className="form-control"
              value={this.state.lastName}
              onChange={this.changeLastNameHandler}
            />
          </div>
          <div>
            <label> Arbitru: </label>
            <Select options={arbitru} />
          </div>
          <div>
            <label> Email: </label>
            <input
              placeholder="Email Address"
              name="email"
              className="form-control"
              value={this.state.emailId}
              onChange={this.changeEmailHandler}
            />
          </div>
          <div>
            <label> Asociatie: </label>
            <Select options={this.state.selectOptions} />
          </div>

          <button
            className="btn btn-success"
            onClick={this.saveOrUpdateEmployee}
          >
            Save
          </button>
        </form>
      </div>
    );
  }
}

export default AdminComponent;
