  };

  render() {
    function importAll(r) {
      return r.keys().map(r);
    }

    const images = importAll(require.context("./", false, /\.(png|jpeg|svg)$/));

    return (
      <div className="fajtaContainer">
        {this.state.fajtak.map((fajta) => (
          <article className="initialBox" key={fajta.id}>
            <div>
              <img
                className="fajtaImg"
                src={"../img/fajtak/" + fajta.kep}
                alt=""
              />
            </div>
            <div className="leiras">
              <h2>{fajta.fajta}</h2>
              <div className="szoveg"></div>

              <button
                id={fajta.id}
                onClick={this.routeChange}
                className="button"
              >
                <NavLink
                  activeClassName="active"
                  to={`/fajtaleirasok/${fajta.id}`}
                >
                  Olvass tobbet
                </NavLink>
              </button>
            </div>
          </article>
        ))}
      </div>
    );
  }
}

export default FajtaComponent;
