import { Splide, SplideSlide } from "@splidejs/react-splide";
import React, { Component } from "react";
import kep1 from "../img/album/20200204_113822.jpg";
import kep2 from "../img/album/20200204_113950.jpg";
import kep3 from "../img/album/20200204_114059.jpg";

class SplideComponent extends Component {
  constructor(props) {
    super(props);
    this.primaryRef = React.createRef();
    this.secondaryRef = React.createRef();
  }

  componentDidMount() {
    // Set the sync target right after the component is mounted.
    this.primaryRef.current.sync(this.secondaryRef.current.splide);
  }
  componentDidUpdate() {
    this.primaryRef.current.sync(this.secondaryRef.current.splide);
  }
  render() {

    return (
      <div>
        <div id="image-slider" class="splide">
          <div class="splide__track">
            <ul class="splide__list">
              <li class="splide__slide">
                <img src="image01.jpg" />
              </li>
              <li class="splide__slide">
                <img src="image02.jpg" />
              </li>
              <li class="splide__slide">
                <img src="image03.jpg" />
              </li>
            </ul>
          </div>
        </div>

        <div id="secondary-slider" class="splide">
          <div class="splide__track">
            <ul class="splide__list">
              <li class="splide__slide">
                <img src="thumbnail01.jpg" />
              </li>
              <li class="splide__slide">
                <img src="thumbnail02.jpg" />
              </li>
              <li class="splide__slide">
                <img src="thumbnail03.jpg" />
              </li>
            </ul>
          </div>
        </div>
      </div>
    );
  }
}

export default SplideComponent;
