import React, { Component } from "react";
import { Link } from "react-router-dom";

class RegisterComponent extends Component {
  constructor(props) {
    super(props);
    state = {
      helyseg: null,
    };
  }
  componentDidMount() {
    this.getoptions();
  }
  async getOptions() {
    const res = await axios.get("http://localhost:8090/api/helysegek");
    const data = res.data;
    console.log(data);
    const options = data.map((response) => ({
      value: response.fajta,
      label: response.fajta,
    }));
    this.setState({ fajtak: options });
    console.log(options);
  }
  render() {
    return (
      <div>
        <h2>Înregistrare</h2>
        <input placeholder="Numele și prenumele"></input>
        <input placeholder="Adresa"></input>
        <input placeholder="Nr telefon"></input>
        <select
          value={this.state.helyseg.value}
          onChange={this.changeFajtaHandler}
        >
          {this.state.helyseg.map((option) => (
            <option value={option.value}>{option.label}</option>
          ))}
        </select>
        <input placeholder="Email"></input>
        <input placeholder="Password"></input>
        <input placeholder="Password"></input>
        <input placeholder=""></input>
        <input placeholder=""></input>
      </div>
    );
  }
}

export default RegisterComponent;
