import React, { Component } from "react";
import Select from "react-select";
import axios from "axios";
import AdminPanelService from "../../service/AdminPanelService";
import AllatokService from "../../service/AllatokService";

class AddAllatComponent extends Component {
  constructor(props) {
    super(props);
    this.goBack = this.goBack.bind(this);

    this.state = {
      id: this.props.match.params.id,
      balful: "",
      jobbful: "",
      nem: "",
      fajta_id: "",
      tulaj_id: "",
    };
  }
  goBack() {
    this.props.history.goBack();
  }
  componentDidMount() {
    console.log(this.state.id);
    if (this.state.id === "_add") {
      this.getOptions();
      return;
    } else {
      AllatokService.getKisallatById(this.state.id).then((res) => {
        let tulaj = res.data;
        console.log(res.data);
        this.setState({
          id: res.data.id,
          balful: res.data.balful,
          jobbful: res.data.jobbful,
          nem: res.data.nem,
          fajta_id: res.data.fajta_id,
          tulaj_id: res.data.tulaj_id,
        });
      });
      this.getOptions();
    }
  }

  saveOrUpdateAllat = (e) => {
    e.preventDefault();
    let tulajUpdate = {
      id: this.state.id,
      balful: this.state.balful,
      jobbful: this.state.jobbful,
      nem: this.state.nem,
      fajta_id: this.state.fajta_id,
      tulaj_id: this.state.tulaj_id,
    };
    let tulajAdd = {
      balful: this.state.balful,
      jobbful: this.state.jobbful,
      nem: this.state.nem,
      fajta_id: this.state.fajta_id,
      tulaj_id: this.state.tulaj_id,
    };
    console.log("tulaj => " + JSON.stringify(tulajAdd));
    console.log("tulaj => " + JSON.stringify(tulajUpdate));

    if (this.state.id === "_add") {
      AllatokService.addKisallat(kisallatAdd).then((res) => {});
      this.goBack();
    } else {
        AllatokService.updateTenyeszto(
        tulajUpdate,
        this.state.id
      ).then((res) => {});
      this.goBack();
    }
  };

  changebalfulHandler = (event) => {
    this.setState({ balful: event.target.value });
  };

  changejobbfulHandler = (event) => {
    this.setState({ jobbful: event.target.value });
  };
  changeTelHandler = (event) => {
    this.setState({ nem: event.target.value });
  };
  changefajta_idHandler = (event) => {
    this.setState({ fajta_id: event.target.value });
    console.log(event.target.value);
  };
  changeEgyesuletHandler = (event) => {
    this.setState({ tulaj_id: event.target.value });
    console.log(event.target.value);
  };

  async getOptions() {
    const res = await axios.get("http://localhost:8090/api/egyesulet");
    const data = res.data;

    const options = data.map((response) => ({
      value: response.id,
      label: response.balful,
    }));
    this.setState({ selectOptions: options });
    console.log(options);
  }
  render() {
    if (!this.state.selectOptions.length) return null;

    let egyesuletek = this.state.selectOptions;
    const arbitru = [
      { value: "true", label: "Da" },
      { value: "false", label: "Nu" },
    ];

    return (
      <div>
        <form class="addForm">
          <div>
            <label> Nume: </label>
            <input
              placeholder="Nume"
              name="nume"
              className="form-control"
              value={this.state.balful}
              onChange={this.changebalfulHandler}
            />
          </div>
          <div>
            <label> Adresa: </label>
            <input
              placeholder="Adresa"
              name="adresa"
              className="form-control"
              value={this.state.jobbful}
              onChange={this.changejobbfulHandler}
            />
          </div>
          <div>
            <label> Nr tel: </label>
            <input
              placeholder="Nr tel"
              name="telefon"
              className="form-control"
              value={this.state.nem}
              onChange={this.changeTelHandler}
            />
          </div>
          <div>
            <label> Arbitru: </label>
            <select
              placeholder={this.state.fajta_id == 0 ? "nu" : "da"}
              onChange={this.changefajta_idHandler}
            >
              {arbitru.map((option) => (
                <option value={option.value}>{option.label}</option>
              ))}
            </select>
          </div>

          <div>
            <label> Asociatie: </label>
            <select
              value={this.state.selectOptions.value}
              onChange={this.changeEgyesuletHandler}
            >
              {this.state.selectOptions.map((option) => (
                <option value={option.value}>{option.label}</option>
              ))}
            </select>
          </div>
          <button className="btn btn-success" onClick={this.saveOrUpdateTulaj}>
            Save
          </button>
        </form>
      </div>
    );
  }
}

export default AddAllatComponent;
