import React, { Component } from "react";
import EgyesuletekService from "../service/EgyesuletekService";
import KeresService from "../service/KeresService";

class Jovahagyas extends Component {
  constructor(props) {
    super(props);

    this.state = {
      egyesuletek: [],
      keresek: [],
    };
  }
  componentDidMount() {
    KeresService.getKeresById()
      .then((res) => {
        this.setState({ keresek: res.data });
      })
      .catch(function (ex) {
        console.log("Response parsing failed. Error: ", ex);
      });
    EgyesuletekService.getEgyesuletek()
      .then((res) => {
        this.setState({ egyesuletek: res.data });
        console.log(this.state.data);
      })
      .catch(function (ex) {
        console.log("Response parsing failed. Error: ", ex);
      });
  }
  render() {
    return (
      <div>
        <div class="jovahagyas">
          <input type="text" />
          <input type="text" />
          <button class="infoBtnAdd">jovahagy</button>
        </div>
        <table className="keresekTable">
          <thead>
            <tr>
              <th>Jobbful</th>
              <th>Balful</th>
              <th>Anyajobbful</th>
              <th>Anyabalful</th>
              <th>Apajobbful</th>
              <th>Apabalful</th>
              <th>bakok</th>
              <th>nostenyek</th>
              <th>fajta</th>
              <th>szuldat</th>
              <th>tenyeszto</th>
            </tr>
          </thead>
          <tbody>
            {this.state.egyesuletek.map((egyesulet) => (
              <tr>
                <td>{egyesulet.azonosito}</td>
                <td>{this.props.match.params.id}</td>
                <td>{this.ker}</td>
                <td></td>
                <td></td>
                <td></td>
                <td></td>
                <td></td>
                <td></td>
                <td></td>
                <td></td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  }
}

export default Jovahagyas;
