import React, { Component } from "react";
import HirdetesService from "../service/HirdetesService";
import KepService from "../service/KepService";
import axios from "axios";

class KeresKuldComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      ar: "",
      db: "",
      nev: "",
      leiras: "",
      tulajId: "",
      selectOptions: [],
    };
  }
  componentDidMount() {
    this.getOptions();
  }
  onFileChangeHandler = (e) => {
    e.preventDefault();
    this.setState({
      selectedFile: e.target.files[0],
    });
    const formData = new FormData();
    formData.append("file", this.state.selectedFile);
    KepService.upload(formData).then((res) => {
      console.log(formData);
      console.log(this.state.selectedFile);
      alert("File uploaded successfully.");
    });
  };
  async getOptions() {
    const res = await axios.get("http://localhost:8090/api/fajta");
    const data = res.data;

    const options = data.map((response) => ({
      value: response.id,
      label: response.fajta,
    }));
    this.setState({ selectOptions: options });
  }
  saveHirdetes = (e) => {
    e.preventDefault();
    let hirdetes = {
      ar: this.state.ar,
      db: this.state.db,
      nev: this.state.nev,
      leiras: this.state.leiras,
      tulajId: this.state.tulajId,
      // pictureAsFile: e.target.files[0],
    };
    // console.log(this.pictureAsFile);
    // const formData = new FormData();
    // formData.append("file", this.state.pictureAsFile);
    // KepService.upload(this.pictureAsFile).then((res) => {});
    HirdetesService.addHirdetes(hirdetes).then((res) => {});
  };

  changeNevHandler = (event) => {
    this.setState({ nev: event.target.value });
  };

  changeArHandler = (event) => {
    this.setState({ ar: event.target.value });
  };

  changeDbHandler = (event) => {
    this.setState({ db: event.target.value });
  };

  changeLeirasHandler = (event) => {
    this.setState({ leiras: event.target.value });
  };

  changeNevHandler = (event) => {
    this.setState({ Nev: event.target.value });
  };
  render() {
    return (
      <div className="keresContainer">
        <div className="keresDiv">
          <div>
            <h2>hirdetes feladasa</h2>
            <div className="hirdetDiv">
              <input
                type="text"
                name="cim"
                id=""
                placeholder="Hirdetes cime"
                onChange={this.changeNevHandler}
              />
            </div>
            <div className="hirdetDiv">
              <input
                type="text"
                name="ar"
                id=""
                placeholder="Ar"
                onChange={this.changeArHandler}
              />
            </div>
            <div className="hirdetDiv">
              <input
                type="text"
                name="db"
                id=""
                placeholder="Db"
                onChange={this.changeDbHandler}
              />
            </div>
            <div>
              <textarea
                name=""
                id=""
                cols="30"
                rows="10"
                placeholder="Hirdetes leirasa"
                onChange={this.changeLeirasHandler}
              ></textarea>
            </div>
          </div>
          <div>
            <h2>Kepfeltoltes</h2>
            <input
              type="file"
              className="form-control"
              name="file"
              onChange={this.onFileChangeHandler}
            />
          </div>
          <div>
            <button onClick={this.saveHirdetes}>Trimite</button>
          </div>
        </div>
      </div>
    );
  }
}

export default KeresKuldComponent;
