import React, { Component } from "react";
import AllatokService from "../service/AllatokService";
import TenyesztokService from "../service/TenyesztokService";
import TulajokService from "../service/TulajokService";
import axios from "axios";

class ProfilComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      id: this.props.match.params.id,
      tenyesztok: {},
      tulajok: {},
      kisallatok: {},
      kiallitasok: {},
    };
  }

  componentDidMount() {
    TenyesztokService.getTenyesztoById(this.state.id)
      .then((res) => {
        this.setState({ tenyesztok: res.data });
        console.log(this.state.tenyesztok);
        // console.log(config1)
      })
      .catch(function (ex) {
        console.log("Response parsing failed. Error: ", ex);
      });
    TulajokService.getTulajok()
      .then((res) => {
        this.setState({ tulajok: res.data });
        console.log(this.state.tulajok);
      })
      .catch(function (ex) {
        console.log("Response parsing failed. Error: ", ex);
      });
    axios
      .get(`http://localhost:8090/api/kisallat/profil/` + this.state.id)
      .then((res) => {
        this.setState({ kisallatok: res.data });
      });
    if (typeof this.state.kisallatok.id != "undefined") {
      axios
        .get(
          `http://localhost:8090/api/kresztvesz/get/` + this.state.kisallatok.id
        )
        .then((res) => {
          this.setState({ kiallitasok: res.data });
          console.log(this.state.kiallitasok);
        });
    }
    // AllatokService.getKisallatByTulajId(this.state.id)
    //   .then((res) => {
    //     console.log("itt meg el");

    //     this.setState({ kisallatok: res.data });
    //     console.log(this.state.kisallatok);
    //   })
    //   .catch(function (ex) {
    //     console.log("Response parsing failed. Error: ", ex);
    //   });
  }
  render() {
    // if (typeof this.state.kisallatok.id == "undefined") return "";
    if (typeof this.state.kiallitasok.id == "undefined") return "";

    return (
      <div>
        <div>
          <h2>{this.state.tenyesztok.nev}</h2>
          <h4>Adresa: {this.state.tenyesztok.cim}</h4>
          <h4>Asociatia: {this.state.tenyesztok.egyesulet.nev}</h4>
          <h4>Rase detinute: </h4>
          <div>{this.state.kisallatok.fajta.fajta}</div>
          <div>{this.state.kiallitasok.pontszam}</div>
        </div>
      </div>
    );
  }
}

export default ProfilComponent;
