  constructor(props) {
    super(props);
    this.state = {
      fajtak: [],
      counted: {},
      loaded: false,
    };
  }
  componentDidMount() {
    FajtaService.getFajtak()
      .then((res) => {
        this.setState({ fajtak: res.data });
        console.log(this.state.data);
      })
      .catch(function (ex) {
        console.log("Response parsing failed. Error: ", ex);
      });
  }
  render() {
    console.log(this.state.fajtak.length);
