                <tr key={elem.id}>
                  <td>{elem.id}</td>
                  {/* <td>{response.data}</td> */}
              {/* </tr>;
                // }
                //   })
                //   .catch(function (error) {
                //     console.log(error);
                //   });
              })} */}
              {/* {this.state.counted.map((count) => (
              <tr key={count.fajta}>
                <td>{count.fajta}</td>
                <td>{count.number}</td>
              </tr>
            ))} */}{" "}
              {/**/} */}
            </tbody>
          </table>
        </div>
      );
    else return null;
  }
}

export default StatisztikaComponent;
