import React, { Component } from "react";

class KeresKuldComponent extends Component {
  constructor(props) {
    this.state = {};
  }
  onFileChangeHandler = (e) => {
    e.preventDefault();
    this.setState({
        selectedFile: e.target.files[0]
    });
    const formData = new FormData();
    formData.append('file', this.state.selectedFile);
    ApiService.upload(formData)
        .then(res => {
                console.log(res.data);
                alert("File uploaded successfully.")
        })
};
  render() {
    return (
      <div className="keresContainer">
        <div className="keresDiv">
          <div>
            <h2>hirdetes feladasa</h2>
            <div className="hirdetDiv">
              <input type="text" name="cim" id="" value="Hirdetes cime" />
            </div>
            <div className="hirdetDiv">
              <input type="text" name="ar" id="" value="Ar" />
            </div>
            <div className="hirdetDiv">
              <input type="text" name="db" id="" value="Db" />
            </div>
            <div>
              <textarea
                name=""
                id=""
                cols="30"
                rows="10"
                value="Hirdetes leirasa"
              ></textarea>
            </div>
          </div>
          <div>
            <h2>Kepfeltoltes</h2>
            <input
              type="file"
              className="form-control"
              name="file"
              onChange={this.onFileChangeHandler}
            />
          </div>
        </div>
      </div>
    );
  }
}

export default KeresKuldComponent;
