import React, { PureComponent } from "react";
import Geocoder from "react-mapbox-gl-geocoder";
import ReactMapGL, { Marker } from "react-map-gl";

const mapStyle = {
  width: "100%",
  height: 600,
};

const mapboxApiKey =
  "pk.eyJ1IjoicHVza2k5NiIsImEiOiJja25hOTJzeTAwenRjMnJ0YXQwMzRpNWV4In0.ElYpo9WdpgCbQsFCYrBsvg";

const params = {
  country: "ca",
};
const CustomMarker = ({ index, marker }) => {
  return (
    <Marker longitude={marker.longitude} latitude={marker.latitude}>
      <div className="marker">
        <span>
          <b>{index + 1}</b>
        </span>
      </div>
    </Marker>
  );
};
class MapView extends PureComponent {
  constructor(props) {
    super(props);
    this.state = {
      viewport: {
        latitude: 45.50884,
        longitude: -73.58781,
        // zoom: 15,
      },
      tempMarker: null,
      markers: [],
    };
  }
  // componentDidMount() {
  //   navigator.geolocation.getCurrentPosition((position) => {
  //     let newViewport = {
  //       latitude: position.coords.latitude,
  //       longitude: position.coords.longitude,
  //       zoom: 12,
  //     };
  //     this.setState({
  //       viewport: newViewport,
  //     });
  //   });
  // }
  onSelected = (viewport, item) => {
    this.setState({
      viewport,
    });
  };
  // add = () => {
  //   var { tempMarker } = this.state;

  //   this.setState((prevState) => ({
  //     markers: [...prevState.markers, tempMarker],
  //     tempMarker: null,
  //   }));
  // };
  render() {
    const geojson = {
      'type': 'FeatureCollection',
      'features': [
      {
      'type': 'Feature',
      'properties': {
      'message': 'Foo',
      'iconSize': [60, 60]
      },
      'geometry': {
      'type': 'Point',
      'coordinates': [-66.324462890625, -16.024695711685304]
      }
      },
      {
      'type': 'Feature',
      'properties': {
      'message': 'Bar',
      'iconSize': [50, 50]
      },
      'geometry': {
      'type': 'Point',
      'coordinates': [-61.2158203125, -15.97189158092897]
      }
      },
      {
      'type': 'Feature',
      'properties': {
      'message': 'Baz',
      'iconSize': [40, 40]
      },
      'geometry': {
      'type': 'Point',
      'coordinates': [-63.29223632812499, -18.28151823530889]
      }
      }
      ]
      };
    const { viewport } = this.state;
    const map = new ReactMapGL.map({
      container: "map",
      style: "mapbox://styles/mapbox/streets-v11",
      center: [-65.017, -16.457],
      zoom: 5,
    });
    geojson.features.forEach(function (marker) {
      // create a DOM element for the marker
      var el = document.createElement("div");
      el.className = "marker";
      el.style.backgroundImage =
        "url(https://placekitten.com/g/" +
        marker.properties.iconSize.join("/") +
        "/)";
      el.style.width = marker.properties.iconSize[0] + "px";
      el.style.height = marker.properties.iconSize[1] + "px";
      el.style.backgroundSize = "100%";

      el.addEventListener("click", function () {
        window.alert(marker.properties.message);
      });

      // add marker to map
      new Marker(el).setLngLat(marker.geometry.coordinates).addTo(map);
    });
    return (
      <div>
        <Geocoder
          mapboxApiAccessToken={mapboxApiKey}
          onSelected={this.onSelected}
          viewport={viewport}
          hideOnSelect={true}
          value=""
          queryParams={params}
        />
        <ReactMapGL
          mapboxApiAccessToken={mapboxApiKey}
          mapStyle="mapbox://styles/mapbox/streets-v11"
          {...viewport}
          {...mapStyle}
          onViewportChange={(viewport) => this.setState({ viewport })}
        >
          <Marker
            latitude={this.state.viewport.latitude}
            longitude={this.state.viewport.longitude}
          >
            <img className="marker" />
          </Marker>
          {this.state.markers.map((marker, index) => {
            return (
              <CustomMarker
                key={`marker-${index}`}
                index={index}
                marker={marker}
              />
            );
          })}
        </ReactMapGL>
      </div>
    );
  }
}

export default MapView;
