import React, { Component } from "react";
import Select from "react-select";
import axios from "axios";
import AdminPanelService from "../service/AdminPanelService";
import TenyesztokService from "../service/TenyesztokService";
import KeresService from "../service/KeresService";

class KeresKuldComponent extends Component {
  constructor(props) {
    super(props);

    this.state = {
      anyabalful: "",
      anyajobbful: "",
      apabalful: "",
      apajobbful: "",
      bakok: "",
      nostenyek: "",
      szuldat: "",
      fajta: "",
      szin: "",
      nev: "",
      tenyesztok: [],
      fajtak: [],
    };
  }
  componentDidMount() {
    console.log(this.state.id);
    this.getOptions();
    return;
  }

  saveKeres = (e) => {
    e.preventDefault();
    let keres = {
      anyabalful: this.state.anyabalful,
      anyajobbful: this.state.anyajobbful,
      apabalful: this.state.apabalful,
      apajobbful: this.state.apajobbful,
      bakok: this.state.bakok,
      nostenyek: this.state.nostenyek,
      szuldat: this.state.szuldat,
      fajta: this.state.fajta,
      szin: this.state.szin,
      nev: this.state.nev,
    };
    console.log("keres => " + JSON.stringify(keres));

    KeresService.addKeres(keres).then((res) => {});
  };
  async getOptions() {
    const res = await axios.get("http://localhost:8090/api/tulaj");
    const data = res.data;

    const options = data.map((response) => ({
      value: response.id,
      label: response.nev,
    }));
    this.setState({ selectOptions: options });
    console.log(options);
  }
  changeAnyajobbfulHandler = (event) => {
    this.setState({ anyajobbful: event.target.value });
  };
  changeAnyabalfulHandler = (event) => {
    this.setState({ anyabalful: event.target.value });
  };
  changeApajobbfulHandler = (event) => {
    this.setState({ apajobbful: event.target.value });
  };
  changeApabalfulHandler = (event) => {
    this.setState({ apabalful: event.target.value });
  };
  changeBakokHandler = (event) => {
    this.setState({ bakok: event.target.value });
  };
  changeNostenyekHandler = (event) => {
    this.setState({ nostenyek: event.target.value });
  };
  changeSzuldatHandler = (event) => {
    this.setState({ szuldat: event.target.value });
  };
  changeFajtaHandler = (event) => {
    this.setState({ fajta: event.target.value });
  };
  changeSzinHandler = (event) => {
    this.setState({ szin: event.target.value });
  };
  render() {
    return (
      <div className="keresContainer">
        <div className="keresDiv">
          <div>
            <h3>Tenyeszto adatok</h3>
            <div>
              <input
                type="text"
                name="tenyeszto"
                id=""
                value="Tenyeszto neve"
              />
            </div>
          </div>
          <div>
            <h3>Fajta adatai</h3>
            <div>
              <input type="text" name="Fajta" id="" value="Fajta" onChange={this.changeFajtaHandler} />
            </div>
            <div>
              <input type="text" name="Szin" id="" value="Szin" onChange={this.changeSzinHandler} />
            </div>
          </div>
          <div>
            <h3>Szulok adatai</h3>
            <div>
              <input type="text" name="Anyajobbful" id="" value="Anyajobbful" onChange={this.changeAnyajobbfulHandler} />
            </div>
            <div>
              <input type="text" name="Anyabalful" id="" value="Anyabalful" onChange={this.change} />
            </div>
            <div>
              <input type="text" name="Apajobbful" id="" value="Apajobbful" onChange={this.changeNevHandler} />
            </div>
            <div>
              <input type="text" name="Apabalful" id="" value="Apabalful" onChange={this.changeNevHandler} />
            </div>
          </div>
          <div>
            <h3>Kolykok adatai</h3>
            <div>
              <input type="number" name="bakok" id="" value="Bakok szama" onChange={this.changeNevHandler} />
            </div>
            <div>
              <input
                type="number"
                name="nostenyek"
                id=""
                value="Nostenyek szama"
                onChange={this.changeNevHandler}
              />
            </div>
            <div>
              <input
                type="date"
                name="szuletes"
                id=""
                value="Szuletesi datum"
                onChange={this.changeNevHandler}
              />
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default KeresKuldComponent;
