import React, { Component } from "react";
import Select from "react-select";
import axios from "axios";

class AdminComponent extends Component {
  constructor(props) {
    super(props);

    this.state = {
      // step 2
      id: this.props.match.params.id,
      firstName: "",
      lastName: "",
      emailId: "",
    };
    this.changeFirstNameHandler = this.changeFirstNameHandler.bind(this);
    this.changeLastNameHandler = this.changeLastNameHandler.bind(this);
    this.saveOrUpdateEmployee = this.saveOrUpdateEmployee.bind(this);
  }

  // step 3
  componentDidMount() {
    // step 4
    if (this.state.id === "_add") {
      return;
    } else {
      Admin.getEmployeeById(this.state.id).then((res) => {
        let employee = res.data;
        this.setState({
          firstName: employee.firstName,
          lastName: employee.lastName,
          emailId: employee.emailId,
        });
      });
    }
  }
  saveOrUpdateEmployee = (e) => {
    e.preventDefault();
    let employee = {
      firstName: this.state.firstName,
      lastName: this.state.lastName,
      emailId: this.state.emailId,
    };
    console.log("employee => " + JSON.stringify(employee));

    // step 5
    if (this.state.id === "_add") {
      Admin.createEmployee(employee).then((res) => {
        this.props.history.push("/employees");
      });
    } else {
      Admin.updateEmployee(employee, this.state.id).then((res) => {
        this.props.history.push("/employees");
      });
    }
  };

  changeFirstNameHandler = (event) => {
    this.setState({ firstName: event.target.value });
  };

  changeLastNameHandler = (event) => {
    this.setState({ lastName: event.target.value });
  };

  changeEmailHandler = (event) => {
    this.setState({ emailId: event.target.value });
  };
  async getOptions() {
    const res = await axios.get("http://localhost:8090/api/egyesulet");
    const data = res.data;

    const options = data.map((response) => ({
      value: response.id,
      label: response.nev,
    }));
    this.setState({ selectOptions: options });
    console.log(options);
  }
  componentDidMount() {
    this.getOptions();
  }
  render() {
    const options = [
      { value: "chocolate", label: "Chocolate" },
      { value: "strawberry", label: "Strawberry" },
      { value: "vanilla", label: "Vanilla" },
    ];
    const MyComponent = () => <Select options={options} />;
    return (
      <div>
        <form>
          <div className="form-group">
            <label> First Name: </label>
            <input
              placeholder="First Name"
              name="firstName"
              className="form-control"
              value={this.state.firstName}
              onChange={this.changeFirstNameHandler}
            />
          </div>
          <div className="form-group">
            <label> Last Name: </label>
            <input
              placeholder="Last Name"
              name="lastName"
              className="form-control"
              value={this.state.lastName}
              onChange={this.changeLastNameHandler}
            />
          </div>
          <div className="form-group">
            <label> Email Id: </label>
            <input
              placeholder="Email Address"
              name="emailId"
              className="form-control"
              value={this.state.emailId}
              onChange={this.changeEmailHandler}
            />
          </div>
          <div>
            <Select options={this.state.selectOptions} />
          </div>

          <button
            className="btn btn-success"
            onClick={this.saveOrUpdateEmployee}
          >
            Save
          </button>
        </form>
      </div>
    );
  }
}

export default AdminComponent;
