import React, { Component } from "react";
import axios from "axios";

class StatisztikaComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      fajtak: [],
      counted: {},
    };
  }
  componentDidMount() {
    axios
      .get("http://localhost:8090/api/fajta")
      .then((res) => {
        console.log(res.data);
        this.setState({ fajtak: res.data });
      })
      .catch(function (error) {
        console.log(error);
      });
      this.state.fajtak.forEach(elem => {
        this.state.fajtak.map((elem) => {
            console.log(elem.id);
            axios
              .get("http://localhost:8090/api/kisallat/counter", {
                params: {
                  id: elem.id,
                },
              })
              .then((response) => {
                let count = {
                  fajta: elem.id,
                  number: response.data,
                };
                this.setState({ counted: response.data });
                console.log(response);
                console.log(this.state.counted);
              });
          });
      });
    

    this.setState({ loaded: true });
    console.log(this.state.counted);
  }
  render() {
    return (
      <div>
        <table>
          <thead>
            <tr>
              <th>Rasa</th>
              <th>Numar</th>
            </tr>
          </thead>
          <tbody>
            {/* {this.state.counted.map((count) => (
              <tr key={count.fajta}>
                <td>{count.fajta}</td>
                <td>{count.number}</td>
              </tr>
            ))} */}
          </tbody>
        </table>
      </div>
    );
  }
}

export default StatisztikaComponent;
