import React, { useState, Component, Route, Router } from "react";
import logo from "../img/logook.jpg";
import HirdetesekComponent from "./HirdetesekComponent";
import { NavLink } from "react-router-dom";
import axios from "axios";
import FacebookLogin from "react-facebook-login";
const [login, setLogin] = useState(false);
const [data, setData] = useState({});
const [picture, setPicture] = useState("");
const responseFacebook = (response) => {
  console.log(response);
  setData(response);
  setPicture(response.picture.data.url);
  if (response.accessToken) {
    setLogin(true);
  } else {
    setLogin(false);
  }
};

class Header extends Component {
  state = {
    activated: false,
    activatedLogin: false,
    body: null,
    headers: null,
    login: null,
    data:null,
    picture:nu
  };
  componentDidMount() {
    console.log("valasz", this.state.response);
  }
  // onClicked() {
  //   this.setState({ responseFacebook });
  //   console.log("compoClk", this.state.response);
  // }
  loginClk() {
    // axios
    //   .post("http://localhost:8081/oauth/token", {
    //     Accept: "application/x-www-form-urlencoded",
    //     // Authorization: "Basic " + btoa("CLIENTID:SECRET"),
    //     client_id: "CLIENTID",
    //     client_secret: "SECRET",
    //     grant_type: "password",
    //     email: "asd@asd.asd",
    //     password: "asdasdasd",
    //     // headers: {
    //     //   Accept: "application/x-www-form-urlencoded",
    //     //   "WWW-Authenticate": "Basic " + btoa("CLIENTID:SECRET"),
    //     // },
    //   })
    //   .then((respose) => {
    //     console.log(respose);
    //   });
  }

  loginMenu = () => {
    this.setState({
      activatedLogin: !this.state.activatedLogin,
    });
    console.log("valasz", this.state.response);
  };
  toggleMenu = () => {
    this.setState({
      activated: !this.state.activated,
    });
  };
  render() {
    const activat = this.state.activated ? "show" : "";
    const activated = this.state.activatedLogin ? "show-login" : "";
    return (
      <div className="header-container">
        <div className="header-row">
          <div id="logo">
            <img src={logo} alt="logo" className="header-pic" />
          </div>

          <div id="menu">
            <nav id="navbar">
              <ul className="menu-items">
                <li>
                  <NavLink activeClassName="active" to={"/"}>
                    Pagina principală
                  </NavLink>
                </li>
                <li>
                  <NavLink activeClassName="active" to={"/fajtaleirasok"}>
                    Descrieri de rasă
                  </NavLink>
                </li>
                <li>
                  <NavLink activeClassName="active" to={"/hirdetesek"}>
                    Anunțuri
                  </NavLink>
                </li>
                <li>
                  <NavLink activeClassName="active" to={"/rolunk"}>
                    Despre noi
                  </NavLink>
                </li>
                <li>
                  <NavLink activeClassName="active" to={"/kapcsolat"}>
                    Contact
                  </NavLink>
                </li>
              </ul>
            </nav>
          </div>
          <div id="right-navbar">
            <i
              className="fa fa-bars"
              id="mobilmenu"
              onClick={this.toggleMenu}
              aria-hidden="true"
            ></i>
            <ul className={`mobil-menu-items${activat}`}>
              <li>
                <NavLink activeClassName="active" to={"/"}>
                  Pagina principală
                </NavLink>
              </li>
              <li>
                <NavLink activeClassName="active" to={"/fajtaleirasok"}>
                  Descrieri de rasă
                </NavLink>
              </li>
              <li>
                <NavLink activeClassName="active" to={"/hirdetesek"}>
                  Anunțuri
                </NavLink>
              </li>
              <li>Despre noi</li>
              <li>
                <NavLink activeClassName="active" to={"/kapcsolat"}>
                  Contact
                </NavLink>
              </li>
            </ul>

            <i
              className="fa fa-user"
              aria-hidden="true"
              id="userIcon"
              onClick={this.loginMenu}
            ></i>
            <ul className={`login-items${activated}`}>
              <div className="loginDiv">
                {!login && (
                  <FacebookLogin
                    appId="562118384400275"
                    autoLoad={true}
                    fields="name,email,picture"
                    scope="public_profile,user_friends"
                    callback={responseFacebook}
                    icon="fa-facebook"
                  />
                )}
                {login && <Image src={picture} roundedCircle />}

                {/* <div>
                  <input type="text" name="userName" id="" value="Username" />
                </div>
                <div>
                  <input
                    type="password"
                    name="password"
                    id=""
                    value="Password"
                    onChange={this.handleChange}
                  />
                </div>
                <div>
                  <button onClick={this.loginClk} className="loginBtn">
                    Login
                  </button>
                </div> */}
              </div>
              <li>
                <NavLink activeClassName="active" to={"/hirdeteseim"}>
                  Adaugă un anunț
                </NavLink>
              </li>
              <li>
                <NavLink activeClassName="active" to={"/tagok"}>
                  Membri
                </NavLink>
              </li>
              <li>
                <NavLink activeClassName="active" to={"/kereskuld"}>
                  Trimite cerere
                </NavLink>
              </li>
              <li>
                <NavLink activeClassName="active" to={"/keresek"}>
                  Cereri
                </NavLink>
              </li>
              <li>
                <NavLink activeClassName="active" to={"/adminpanel"}>
                  Actualizare date
                </NavLink>
              </li>
              <div>
                <button className="logoutBtn">Logout</button>
              </div>
            </ul>
          </div>
        </div>
      </div>
    );
  }
}

export default Header;
