import React, { Component } from "react";
import EgyesuletekService from "../service/EgyesuletekService";
import KeresService from "../service/KeresService";
import axios from "axios";

class Jovahagyas extends Component {
  constructor(props) {
    super(props);

    this.state = {
      id: this.props.match.params.id,
      keresek: [],
      load: "",
    };
  }

  async getKeresek() {
    const res = await axios.get(
      "http://localhost:8090/api/keresek/" + this.state.id
    );
    const data = res.data;

    const options = data.map((response) => ({
      value: response.id,
      label: response.anyajobbful,
    }));
    this.setState({ keresek: options });
    console.log(options);
  }

  render() {
    return (
      <div>
        <div class="jovahagyas">
          <input
            type="text"
            // value={this.state.keresek.tulajdonos.nev}
            // placeholder={this.state.keresek.tulajdonos.nev}
          />
          <input type="text" />
          <button class="infoBtnAdd">jovahagy</button>
        </div>
        <table className="keresekTable">
          <thead>
            <tr>
              <th>Jobbful</th>
              <th>Balful</th>
              <th>Anyajobbful</th>
              <th>Anyabalful</th>
              <th>Apajobbful</th>
              <th>Apabalful</th>
              <th>bakok</th>
              <th>nostenyek</th>
              <th>fajta</th>
              <th>szuldat</th>
              <th>tenyeszto</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td></td>
              <td>{this.props.match.params.id}</td>
              <td>{this.state.keresek.apajobbful}</td>
              <td></td>
              <td></td>
              <td></td>
              <td></td>
              <td></td>
              <td></td>
              <td></td>
              <td></td>
            </tr>
          </tbody>
        </table>
      </div>
    );
  }
}

export default Jovahagyas;
