import React, { Component } from "react";
// import { Splide, SplideSlide } from "@splidejs/react-splide";
import kep1 from "../img/album/20200204_113822.jpg";
import kep2 from "../img/album/20200204_113950.jpg";
import kep3 from "../img/album/20200204_114059.jpg";
import Splide from "@splidejs/splide";
class SliderComponent extends Component {
  constructor(props) {
    super(props);

    this.primaryRef = React.createRef();
    this.secondaryRef = React.createRef();
  }

  componentDidMount() {
    // Set the sync target right after the component is mounted.
    this.primaryRef.current.sync(this.secondaryRef.current.splide);
  }

  render() {
    const primaryOptions = {
      type: "loop",
      width: 800,
      perPage: 2,
      perMove: 1,
      gap: "1rem",
      pagination: false,
    };

    const secondaryOptions = {
      type: "slide",
      rewind: true,
      width: 800,
      gap: "1rem",
      pagination: false,
      fixedWidth: 110,
      fixedHeight: 70,
      cover: true,
      focus: "center",
      isNavigation: true,
      updateOnMove: true,
    };

    return (
      <div>
        <Splide options={primaryOptions} ref={this.primaryRef}>
          <SplideSlide>
            <img src={kep1} alt="" />
          </SplideSlide>
          <SplideSlide>
            <img src={kep2} alt="" />
          </SplideSlide>
          <SplideSlide>
            <img src={kep3} alt="" />
          </SplideSlide>
        </Splide>

        <Splide options={secondaryOptions} ref={this.secondaryRef}>
          <SplideSlide>
            <img src={kep1} alt="" />
          </SplideSlide>
          <SplideSlide>
            <img src={kep2} alt="" />
          </SplideSlide>
          <SplideSlide>
            <img src={kep3} alt="" />
          </SplideSlide>
        </Splide>
      </div>
    );
  }
}
export default SliderComponent;
