import React, { PureComponent } from "react";
import Geocoder from "react-mapbox-gl-geocoder";
import ReactMapGL, { Marker } from "react-map-gl";

const mapStyle = {
  width: "100%",
  height: 600,
};

const mapboxApiKey =
  "pk.eyJ1IjoicHVza2k5NiIsImEiOiJja25hOTJzeTAwenRjMnJ0YXQwMzRpNWV4In0.ElYpo9WdpgCbQsFCYrBsvg";

const params = {
  country: "ca",
};
const CustomMarker = ({ index, marker }) => {
  return (
    <Marker longitude={marker.longitude} latitude={marker.latitude}>
      <div className="marker">
        <span>
          <b>{index + 1}</b>
        </span>
      </div>
    </Marker>
  );
};
class MapView extends PureComponent {
  constructor(props) {
    super(props);
    this.state = {
      viewport: {
        latitude: 45.50884,
        longitude: -73.58781,
        zoom: 15,
      },
      tempMarker: null,
      markers: [],
    };
  }
  // componentDidMount() {
  //   navigator.geolocation.getCurrentPosition((position) => {
  //     let newViewport = {
  //       latitude: position.coords.latitude,
  //       longitude: position.coords.longitude,
  //       zoom: 12,
  //     };
  //     this.setState({
  //       viewport: newViewport,
  //     });
  //   });
  // }
  onSelected = (viewport, item) => {
    this.setState({
      viewport,
    });
  };
  // add = () => {
  //   var { tempMarker } = this.state;

  //   this.setState((prevState) => ({
  //     markers: [...prevState.markers, tempMarker],
  //     tempMarker: null,
  //   }));
  // };state = {
    viewport: {
      width: "100%",
      height: 400,
      latitude: 53.795231,
      longitude: -1.54511300000001,
      zoom: 15
    }

  render() {
    const { pinSize } = this.props;
    const propsData = { latitude: this.props.latitude, longitude: this.props.longitude };
    const mapConfig = merge(this.state.viewport, propsData);

    return (
      <MapGL
        mapboxApiAccessToken={process.env.REACT_APP_MAPBOX_TOKEN}
        onViewportChange={viewport => this.setState({ ...viewport, width: "100%" })}
        {...mapConfig}
      >
        <Marker latitude={this.props.latitude} longitude={this.props.longitude}>
          <Pin size={pinSize} />
        </Marker>
      </MapGL>
    );
  }
}

export default MapView;
