import React, { Component } from "react";
// import { Splide, SplideSlide } from "@splidejs/react-splide";
import kep1 from "../img/album/20200204_113822.jpg";
import kep2 from "../img/album/20200204_113950.jpg";
import kep3 from "../img/album/20200204_114059.jpg";
import Splide from "@splidejs/splide";
class SliderComponent extends Component {
  render() {
    document.addEventListener( 'DOMContentLoaded', function () {
      var secondarySlider = new Splide( '#secondary-slider', {
        fixedWidth  : 100,
        height      : 60,
        gap         : 10,
        cover       : true,
        isNavigation: true,
        focus       : 'center',
        breakpoints : {
          '600': {
            fixedWidth: 66,
            height    : 40,
          }
        },
      } ).mount();
      
      var primarySlider = new Splide( '#primary-slider', {
        type       : 'fade',
        heightRatio: 0.5,
        pagination : false,
        arrows     : false,
        cover      : true,
      } ); // do not call mount() here.
      
      primarySlider.sync( secondarySlider ).mount();
    } );
    return (
      
      <div
        id="secondary-slider"
        className="splide"
        options='{"type":"loop","perPage":3,"autoplay":"true","interval:"400"autoplay: true,
        interval: 400,
        rewind: true,
        fixedWidth: "32% !important",
        fixedHeight: "150px",
        isNavigation: true,
        gap: 10,
        focus: "center",
        pagination: false,
        cover: true,
        breakpoints: {
          600: {
            fixedWidth: 66,
            fixedHeight: 40,
          },
        },}'
      >
        <div className="splide__track">
          <ul className="splide__list">
            <li className="splide__slide">
              <img src={kep1} alt="" />
            </li>
            <li className="splide__slide">
              <img src={kep2} alt="" />
            </li>
            <li className="splide__slide">
              <img src={kep3} alt="" />
            </li>
          </ul>
        </div>
      </div>
    );
  }
}
export default SliderComponent;
