import React, { Component } from "react";
import axios from "axios";
import FajtaService from "../service/FajtaService.js";

class StatisztikaComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      fajtak: [],
      counted: [],
      loaded: false,
    };
  }
  componentDidMount() {
    FajtaService.getFajtak()
      .then((res) => {
        this.setState({ fajtak: res.data });
        for (let i = 0; i <= this.state.fajtak.length; i++) {
          axios
            .get("http://localhost:8090/api/kisallat/counter", {
              params: {
                id: i,
              },
            })
            .then((response) => {
              this.setState({
                counted: {
                  fajta: this.state.fajtak.fajta,
                  number: response.data,
                },
              });
              console.log(this.state.counted);
            })
            .catch(function (error) {
              console.log(error);
            });
          console.log(this.state.counted);
        }
      })
