import axios from "axios";

const kisallatok = "http://localhost:8090/api/kisallat";

class FajtaService {
  getkisallatok() {
    return axios.get(kisallatok);
  }

  getKisallatById(kiallatId) {
    return axios.get(kisallatok + "/" + kiallatId);
  }
  addKisallat(kisallat) {
    return axios.post(kisallatok + "/save", kisallat);
  }
  updateKisallat(kisallat) {
    return axios.post(kisallatok + "/update", kisallat);
  }
  deleteKisallat(kiallatId) {
    return axios.delete(kisallatok + "/" + kiallatId);
  }
  
}

export default new FajtaService();
