import React, { Component } from "react";
import Slider from "./Slider.js";
import FajtaComponent from "./FajtaComponent";
import { Carousel } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import img1 from "../img/isabel.jpg";
import img2 from "../img/hampshirek.png";

class FajtaleirasokComponent extends Component {
  constructor(props) {
    super(props);

    this.sta = {
      fajtak: [],
    };
  }
  filter = { osszes: false, tyukok: false, nyulak: false };
  osszes = () => {
    this.setfilter({
      osszes: !this.filter.osszes,
      tyukok: this.setfilter.tyukok,
      nyulak: this.setfilter.nyulak,
    });
  };
  nyulak = () => {
    this.setfilter({
      nyulak: !this.filter.nyulak,
      osszes: this.setfilter.osszes,
      tyukok: this.setfilter.tyukok,
    });
  };
  tyukok = () => {
    this.setfilter({
      tyukok: !this.setfilter.tyukok,
      nyulak: this.setfilter.nyulak,
      osszes: this.setfilter.osszes,
    });
  };
  componentDidMount() {
    FajtaService.getFajtak()
      .then((res) => {
        this.setfilter({ fajtak: res.data });
        console.log(this.filter.data);
      })
      .catch(function (ex) {
        console.log("Response parsing failed. Error: ", ex);
      });
  }

  render() {
    const osszes = this.filter.osszes ? "show" : "hide";
    const nyulak = this.filter.nyulak ? "show" : "hide";
    const tyukok = this.filter.tyukok ? "show" : "hide";
    function importAll(r) {
      return r.keys().map(r);
    }

    const images = importAll(require.context("./", false, /\.(png|jpeg|svg)$/));
    return (
      <div>
        <hr />
        <Carousel interval={2000}>
          <Carousel.Item>
            <img src={img1} height="600px" width="100%" alt="img1" />
            <div className="in-left">
              <h1>Udvozollek a fajtaleirasoknal</h1>
              <div>
                ide is fog jonni szoveg csak meg nem tudom hogy mit tegyek be,
                valamit ami fontos lehet
              </div>
            </div>
          </Carousel.Item>
          <Carousel.Item>
            <img src={img2} height="600px" width="100%" alt="img1" />
          </Carousel.Item>
        </Carousel>
        <div className="filter">
          <div className="filterBar">
            <div
              className="navbarFilter"
              style={{ justifyContent: "space-evenly" }}
            >
              <ul>
                <li className={`selected${osszes}`}>
                  <a onClick={this.osszes}>Osszes</a>
                </li>
                <li className={`selected${nyulak}`}>
                  <a onClick={this.nyulak}>Nyulak</a>
                </li>
                <li className={`selected${tyukok}`}>
                  <a onClick={this.tyukok}>Tyukok</a>
                </li>
              </ul>
            </div>
          </div>
        </div>
        <div className="fajtaContainer">
          {this.filter.fajtak.map((fajta) => (
            <article className="initialBox" key={fajta.id}>
              <div>
                <img
                  className="fajtaImg"
                  src={"../img/fajtak/" + fajta.kep}
                  alt=""
                />
              </div>
              <div className="leiras">
                <h2>{fajta.fajta}</h2>
                <div className="szoveg"></div>

                <button id={fajta.id} className="button">
                  <Link to={`/fajtaleirasok/${fajta.id}`}>Olvass tobbet</Link>
                </button>
              </div>
            </article>
          ))}
        </div>
      </div>
    );
  }
}

export default FajtaleirasokComponent;
