import React, { Component } from "react";
import kep1 from "../img/album/20200204_113822.jpg";
import HirdetesService from "../service/HirdetesService";
class HirdetesComponent extends Component {
  constructor(props) {
    super(props);

    this.state = {
      hirdetesek: [],
    };
  }

  componentDidMount() {
    HirdetesService.getHirdetesek()
      .then((res) => {
        this.setState({ hirdetesek: res.data });
        console.log(this.state.hirdetesek);
      })
      .catch(function (ex) {
        console.log("Response parsing failed. Error: ", ex);
      });
  }

  render() {
    return (
      <div className="containerAd">
        {this.state.hirdetesek.map((hirdetes) => (
          <div className="oneRow">
            <div className="row">
              <div className="leftContent">
                <div className="img">
                  <img src={kep1} alt="" />
                </div>
                <div className="details">
                  <h3 className="leftCName">{hirdetes.nev}</h3>
                  <span className="info">
                    Leiras valami majd itt is lesz, csak kivancsi vagyok
                    hosszabb szoveget hogy kezel, de eddig ugy tunik hogy eleg
                    hosszu szoveget be lehet irni
                  </span>
                  <br />
                  <span className="info"> Hany db van</span>
                  <span className="info">Mennyi az ara</span>
                </div>
              </div>
              <div className="rigthContent">
                <div className="contactInfo">
                  <p>Tulaj neve</p>
                  <p>Tulaj cime</p>
                </div>
                <div className="contact">
                  <div className="message">
                    <a href="">Email</a>
                  </div>
                  <div className="telefon">
                    <a href="">Telefon</a>
                  </div>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
    );
  }
}

export default HirdetesComponent;
