import React, { Component } from "react";
import Select from "react-select";
import axios from "axios";

class AdminComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectOptions: [],
      id: "",
      name: "",
    };
  }
  async getOptions() {
    const res = await axios.get("http://localhost:8090/api/egyesulet");
    const data = res.data;

    const options = data.map((res) => ({
      value: res.id,
      label: res.name,
    }));
    this.setState({ selectOptions: options });
  }
  componentDidMount() {
    this.getOptions();
  }
  render() {
    const options = [
      { value: "chocolate", label: "Chocolate" },
      { value: "strawberry", label: "Strawberry" },
      { value: "vanilla", label: "Vanilla" },
    ];
    const MyComponent = () => <Select options={options} />;
    return (
      <div>
        <h1>Tulaj</h1>
        <table>
          <tr>
            <td>
              <h3>Nev</h3>
            </td>
            <td>
              <input type="text" placeholder="tulajNev" id="tulajNev" />
            </td>
          </tr>
          <tr>
            <td>
              <h3>Cim</h3>
            </td>
            <td>
              <input type="text" placeholder="tulajCim" id="tulajCim" />
            </td>
          </tr>
          <tr>
            <td>
              <h3>Biro</h3>
            </td>
            <td>
              <input type="radio" id="true" name="biro" value="true" />
              <label for="true">Biro</label>
              <br />
              <input type="radio" id="false" name="biro" value="false" />
              <label for="false">Nem biro</label>
              <br />
            </td>
          </tr>
          <tr>
            <td>
              <h3>Egyesulet</h3>
            </td>
            <td>
              <Select options={this.state.selectOptions} />
              Egyesulet
            </td>
          </tr>
          <tr>
            <td></td>
            <td>
              <input
                type="button"
                className="button"
                id="tulajKuld"
                value="tulajKuld"
                onclick="saveTulaj()"
              />
            </td>
          </tr>
        </table>
      </div>
    );
  }
}

export default AdminComponent;
