import React, { Component } from "react";
import axios from "axios";
import FajtaService from "../service/FajtaService.js";

class StatisztikaComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      fajtak: [],
      counted: [],
      loaded: false,
      options: {
        annotations: {
          points: [{
            x: 'Bananas',
            seriesIndex: 0,
            label: {
              borderColor: '#775DD0',
              offsetY: 0,
              style: {
                color: '#fff',
                background: '#775DD0',
              },
              text: 'Bananas are good',
            }
          }]
        },
        chart: {
          height: 350,
          type: 'bar',
        },
        plotOptions: {
          bar: {
            borderRadius: 10,
            columnWidth: '50%',
          }
        },
        dataLabels: {
          enabled: false
        },
        stroke: {
          width: 2
        },
        
        grid: {
          row: {
            colors: ['#fff', '#f2f2f2']
          }
        },
        xaxis: {
          labels: {
            rotate: -45
          },
          categories: ['Apples', 'Oranges', 'Strawberries', 'Pineapples', 'Mangoes', 'Bananas',
            'Blackberries', 'Pears', 'Watermelons', 'Cherries', 'Pomegranates', 'Tangerines', 'Papayas'
          ],
          tickPlacement: 'on'
        },
        yaxis: {
          title: {
            text: 'Servings',
          },
        },
        fill: {
          type: 'gradient',
          gradient: {
            shade: 'light',
            type: "horizontal",
            shadeIntensity: 0.25,
            gradientToColors: undefined,
            inverseColors: true,
            opacityFrom: 0.85,
            opacityTo: 0.85,
            stops: [50, 0, 100]
          },
        }
      },
    };
  }
  componentDidMount() {
    axios
      .get("http://localhost:8090/api/fajta/counter")
      .then((response) => {
        this.setState({
          counted: response.data,
        });
        console.log(this.state.counted);
      })
      .catch(function (error) {
        console.log(error);
      });
    this.setState({ loaded: true });

    console.log(this.state.counted);
  }

  render() {
    console.log(this.state.fajtak.length);
    console.log(this.state.counted.length);

    if (this.state.counted.length != undefined)
      return (
        <div>
          <table className="keresekTable">
            <thead>
              <tr>
                <th>Rasa</th>
                <th>Numar</th>
              </tr>
            </thead>
            <tbody>
              {this.state.counted.map((fajta) => (
                <tr>
                  <td>{fajta.fajta}</td>
                  <td>{fajta.count}</td>
                </tr>
              ))}
              
            </tbody>
          </table>
          <div id="chart">
  <ReactApexChart options={this.state.options} series={this.state.series} type="bar" height={350} />
</div>

        </div>
      );
    else return null;
  }
}

export default StatisztikaComponent;
