import axios from "axios";

const kisallatok = "http://localhost:8090/api/kisallat";

class FajtaService {
  getkisallatok() {
    return axios.get(kisallatok);
  }

  getKisallatById(kiallatId) {
    return axios.get(kisallatok + "/" + kiallatId);
  }
  addEgyesulet(egyesulet) {
    return axios.post(kisallatok + "/save", egyesulet);
  }
  updateEgyesulet(egyesulet) {
    return axios.post(kisallatok + "/update", egyesulet);
  }
  deleteEgyesulet(kiallatId) {
    return axios.delete(kisallatok + "/" + kiallatId);
  }
}

export default new FajtaService();
