import React, { Component } from "react";
import AllatokService from "../../service/AllatokService";
import { Link } from "react-router-dom";
import ReszvetelService from "../../service/ReszvetelService";

class ReszvetelComponent extends Component {
  constructor(props) {
    super(props);

    this.state = {
      reszvetelek: [],
    };
    this.editReszvetel = this.editReszvetel.bind(this);
  }

  editReszvetel(id) {
    this.props.history.push(`/add-participare/${id}`);
  }
  componentDidMount() {
    ReszvetelService.getResztvetelek()
      .then((res) => {
        this.setState({ reszvetelek: res.data });
      })
      .catch(function (ex) {
        console.log("Response parsing failed. Error: ", ex);
      });
    console.log(this.state.reszvetelek);
  }
  deleteKisallat(id) {
    ReszvetelService.deleteResztvetel(id).then((res) => {
      this.setState({
        reszvetelek: this.state.reszvetelek.filter(
          (reszvetel) => reszvetel.id !== id
        ),
      });
      console.log(this.state.reszvetelek);
    });
  }
  render() {
    return (
      <div>
        <button className="infoBtnAdd">
          <Link to={`/add-animale/_add`}>Adauga</Link>
        </button>
        <table className="tagokTable">
          <thead>
            <tr>
              <th>Pontszam</th>

              <th>Allat</th>

              <th>Kiallitas</th>

            </tr>
          </thead>
          <tbody>
            {this.state.allatok.map((allat) => (
              <tr key={allat.id}>
                <td>{allat.balful}</td>
                <td>{allat.jobbful}</td>
                <td>{allat.nem ? "mascul" : "femela"}</td>
                <td>{allat.fajta.fajta}</td>
                <td>
                  <button className="infoBtn" id={allat.id}>
                    <Link to={`/add-animale/${allat.id}`}>Update</Link>
                  </button>
                  <button
                    className="infoBtn"
                    onClick={() => this.deleteKisallat(allat.id)}
                    id={allat.id}
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  }
}

export default ReszvetelComponent;
