import React, { Component } from "react";
import axios from "axios";
import FajtaService from "../service/FajtaService.js";
import Chart from "react-apexcharts";

class StatisztikaComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      fajtak: [],
      counted: [],
      loaded: false,
      options: {
        chart: {
          id: "apexchart-example",
        },
        xaxis: {
          categories: [""],
        },
      },
      series: [
        {
          name: "series-1",
          data: [3],
        },
      ],

      options1: {
        chart: {
          id: "apexchart-example",
        },
        xaxis: {
          categories: [""],
        },
      },
      series1: [
        {
          name: "series-1",
          data: [3],
        },
      ],
    };
  }
  componentDidMount() {
    axios
      .get("http://localhost:8090/api/fajta/counter")
      .then((response) => {
        this.setState({
          counted: response.data,
        });
        console.log(this.state.counted);
        console.log(this.state.options);

        let chartData = [];
        let chartCategories = [];
        let chartSeries = [];
        console.log("for elott", response.data);
        for (let i = 0; i < this.state.counted.length; i++) {
          console.log(response.data[i]);
          chartData.push(response.data[i].count);
          chartCategories.push(response.data[i].fajta);
          chartSeries.push({
            data: [response.data[i].count],
            name: response.data[i].fajta,
          });
        }
        console.log("ezt kene kirajzolni", chartSeries);
        this.setState({
          series: chartSeries,
        });
        this.setState({
            series: chartSeries,
          });
        // this.state.series.data = chartData;
        // this.state.options.xaxis.categories = chartCategories;
        // this.setState({
        //   series: {
        //     data: chartData,
        //   },
        //   options: { xaxis: { categories: chartCategories } },
        // });
      })
      .catch(function (error) {
        console.log(error);
      });
    // if (this.state.counted.length == 0) this.setState({ loaded: true });
    // else {

    console.log(this.state.options);
  }

  render() {
    console.log(this.state.fajtak.length);
    console.log(this.state.counted.length);

    if (this.state.counted.length != undefined)
      return (
        <div>
          <table className="keresekTable">
            <thead>
              <tr>
                <th>Rasa</th>
                <th>Numar</th>
              </tr>
            </thead>
            <tbody>
              {this.state.counted.map((fajta) => (
                <tr>
                  <td>{fajta.fajta}</td>
                  <td>{fajta.count}</td>
                </tr>
              ))}
            </tbody>
          </table>
          <div id="chart">
            <Chart
              options={this.state.options}
              series={this.state.series}
              type="bar"
              width={500}
              height={320}
            />
          </div>
        </div>
      );
    else return null;
  }
}

export default StatisztikaComponent;
