import React, { Component } from 'react';

class StatisztikaComponent extends Component {
    constructor(props) {
        super(props);
        this.state={
            fajtak:[],
            counted:[],
        }
      }
    componentDidMount() {
        axios
          .get("http://localhost:8090/api/fajta")
          .then((res) => {
            console.log(res.data);
            this.setState({ fajtak: res.data });
            this.state.fajtak.map((elem) => {
              console.log(elem.id);
              axios
                .get("http://localhost:8090/api/kisallat/counter", {
                  params: {
                    id: elem.id,
                  },
                })
                .then((response) => {
                  let count = {
                    fajta: elem.id,
                    number: response.data,
                  };
                  this.setState({ counted: count });
                  // console.log(this.state.counted);
                });
            });
          })
          .catch(function (error) {
            console.log(error);
          });
        this.setState({ loaded: true });
        // console.log(this.state.counted);
      }
    render() {
        return (
            <div>
                
            </div>
        );
    }
}

export default StatisztikaComponent;
