import React, { Component } from "react";
import Select from "react-select";
import axios from "axios";
import AdminPanelService from "../../service/AdminPanelService";
import EgyesuletekService from "../../service/EgyesuletekService";

class AdminComponent extends Component {
  constructor(props) {
    super(props);

    this.state = {
      id: this.props.match.params.id,
      nev: "",
      helyseg: "",
      elnok: "",
    };
    console.log(this.state.selectOptions);
  }
  componentDidMount() {
    console.log(this.state.id);
    if (this.state.id === "_add") {
      return;
    } else {
      EgyesuletekService.getEgyesuletById(this.state.id).then((res) => {
        let egyesulet = res.data;
        console.log(res.data);
        this.setState({
          id: res.data.id,
          nev: res.data.nev,
          helyseg: res.data.helyseg,
          elnok: res.data.elnok,
        });
        console.log(egyesulet);
      });
    }
    console.log(this.state);
  }

  saveOrUpdateEgyesulet = (e) => {
    e.preventDefault();
    let egyesuletUpdate = {
      id: this.state.id,
      nev: this.state.nev,
      helyseg: this.state.helyseg,
      elnok: this.state.elnok,
    };
    let egyesuletAdd = {
      nev: this.state.nev,
      helyseg: this.state.helyseg,
      elnok: this.state.elnok,
    };
    console.log("egyesulet => " + JSON.stringify(egyesuletAdd));
    console.log("egyesulet => " + JSON.stringify(egyesuletUpdate));

    if (this.state.id === "_add") {
      EgyesuletekService.addEgyesulet(egyesuletAdd).then((res) => {});
    } else {
      EgyesuletekService.updateEgyesulet(
        egyesuletUpdate,
        this.state.id
      ).then((res) => {});
    }
  };

  changeNevHandler = (event) => {
    this.setState({ nev: event.target.value });
  };

  changehelysegHandler = (event) => {
    this.setState({ helyseg: event.target.value });
  };
  changeElnokHandler = (event) => {
    this.setState({ elnok: event.target.value });
  };
    
  render() {
    return (
      <div>
        <form class="addForm">
          <div>
            <label> Nume: </label>
            <input
              placeholder="Nume"
              name="nume"
              className="form-control"
              value={this.state.nev}
              onChange={this.changeNevHandler}
            />
          </div>
          <div>
            <label> Localitate: </label>
            <input
              placeholder="Localitate"
              name="localitate"
              className="form-control"
              value={this.state.helyseg}
              onChange={this.changehelysegHandler}
            />
          </div>
          <div>
            <label> Nr tel: </label>
            <input
              placeholder="Nr tel"
              name="telefon"
              className="form-control"
              value={this.state.elnok}
              onChange={this.changeTelHandler}
            />
          </div>
          <div>
            <label> Arbitru: </label>
            <select
              placeholder={this.state.biro == 0 ? "nu" : "da"}
              onChange={this.changeBiroHandler}
            >
              {arbitru.map((option) => (
                <option value={option.value}>{option.label}</option>
              ))}
            </select>
          </div>

          <div>
            <label> Asociatie: </label>
            <select
              value={this.state.selectOptions.value}
              onChange={this.changeEgyesuletHandler}
            >
              {this.state.selectOptions.map((option) => (
                <option value={option.value}>{option.label}</option>
              ))}
            </select>
          </div>
          <button className="btn btn-success" onClick={this.saveOrUpdateegyesulet}>
            Save
          </button>
        </form>
      </div>
    );
  }
}

export default AdminComponent;
