import React, { Component } from "react";
import Select from "react-select";
import axios from "axios";
import AdminPanelService from "../../service/AdminPanelService";
import TenyesztokService from "../../service/TenyesztokService";

class AdminComponent extends Component {
  constructor(props) {
    super(props);

    this.state = {
      id: this.props.match.params.id,
      tenyeszto: {},
      nev: "",
      cim: "",
      telszam: "",
      biro: "",
      email: "",
      egyesulet: "",
    };
    console.log(this.state.tenyeszto);
  }
  // componentDidMount() {
  //   TenyesztokService.getTenyesztoById(this.state.id).then((res) => {
  //     this.setState({ tenyeszto: res.data });
  //     console.log(this.state.tenyeszto);
  //     this.getOptions();
  //   });
  // }
  componentDidMount() {
    console.log(this.state.id);
    if (this.state.id === "_add") {
      this.setState({ id: '' });
      console.log(this.state.id);
      return;
    } else {
      TenyesztokService.getTenyesztoById(this.state.id).then((res) => {
        let tulaj = res.data;
        console.log(res.data);
        this.setState({
          id: res.data.id,
          nev: res.data.nev,
          cim: res.data.cim,
          telszam: res.data.telszam,
          biro: res.data.biro,
          email: res.data.email,
          egyesulet: res.data.egyesulet,
          // tenyeszto: res.data,
        });
        console.log(tulaj);
      });
      this.getOptions();
    }
  }

  saveOrUpdateTulaj = (e) => {
    e.preventDefault();
    let tulaj = {
      id: this.state.id,
      nev: this.state.nev,
      cim: this.state.cim,
      telszam: this.state.telszam,
      biro: this.state.biro,
      email: this.state.email,
      egyesulet: this.state.egyesulet,
    };
    console.log("tulaj => " + JSON.stringify(tulaj));

    if (this.state.id === "_add") {
      TenyesztokService.addTenyeszto(tulaj).then((res) => {});
    } else {
      TenyesztokService.updateTenyeszto(tulaj, this.state.id).then((res) => {});
    }
  };

  changeNevHandler = (event) => {
    this.setState({ nev: event.target.value });
  };

  changeCimHandler = (event) => {
    this.setState({ cim: event.target.value });
  };
  changeTelHandler = (event) => {
    this.setState({ telszam: event.target.value });
  };

  changeEmailHandler = (event) => {
    this.setState({ email: event.target.value });
  };

  async getOptions() {
    const res = await axios.get("http://localhost:8090/api/egyesulet");
    const data = res.data;

    const options = data.map((response) => ({
      value: response.id,
      label: response.nev,
    }));
    this.setState({ selectOptions: options });
    console.log(options);
  }
  render() {
    const arbitru = [
      { value: "da", label: "Da" },
      { value: "nu", label: "Nu" },
    ];
    return (
      <div>
        <form class="addForm">
          <div>
            <label> Nume: </label>
            <input
              placeholder="Nume"
              name="nume"
              className="form-control"
              value={this.state.nev}
              onChange={this.changeNevHandler}
            />
          </div>
          <div>
            <label> Adresa: </label>
            <input
              placeholder="Adresa"
              name="adresa"
              className="form-control"
              value={this.state.cim}
              onChange={this.changeCimHandler}
            />
          </div>
          <div>
            <label> Nr tel: </label>
            <input
              placeholder="Nr tel"
              name="telefon"
              className="form-control"
              value={this.state.telszam}
              onChange={this.changeTelHandler}
            />
          </div>
          <div>
            <label> Arbitru: </label>
            <Select
              placeholder={this.state.biro == 0 ? "nu" : "da"}
              options={arbitru}
            />
          </div>
          <div>
            <label> Email: </label>
            <input
              placeholder="Email"
              name="email"
              className="form-control"
              value={this.state.email}
              onChange={this.changeEmailHandler}
            />
          </div>
          <div>
            <label> Asociatie: </label>
            <Select
              placeholder={this.state.egyesulet.nev}
              options={this.state.selectOptions}
            />
          </div>

          <button className="btn btn-success" onClick={this.saveOrUpdateTulaj}>
            Save
          </button>
        </form>
      </div>
    );
  }
}

export default AdminComponent;
