import React, { Component } from "react";
import axios from "axios";
import FajtaService from "../service/FajtaService.js";

class StatisztikaComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      fajtak: [],
      counted: [],
      loaded: false,
    };
  }
  componentDidMount() {
    axios
      .get("http://localhost:8090/api/fajta/counter")
      .then((response) => {
        this.setState({
          counted: response.data,
        });
        console.log(this.state.counted);
      })
      .catch(function (error) {
        console.log(error);
      });
    this.setState({ loaded: true });

    console.log(this.state.counted);
  }

  render() {
    console.log(this.state.fajtak.length);
    console.log(this.state.counted.length);

    if (this.state.counted.length != undefined)
      return (
        <div>
          <table>
            <thead>
              <tr>
                <th>Rasa</th>
                <th>Numar</th>
              </tr>
            </thead>
            <tbody>
              {this.state.counted.map((fajta) => (
                <tr>
                  <td>{fajta.fajta}</td>
                  <td>{fajta.count}</td>
                </tr>
