import React, { PureComponent } from "react";
import ReactMapGL from "react-map-gl";
// import { Container, Col, Row } from "reactstrap";

const mapStyle = {
  width: "100%",
  height: 600,
};

const mapboxApiKey =
  "pk.eyJ1IjoicHVza2k5NiIsImEiOiJja25hOTJzeTAwenRjMnJ0YXQwMzRpNWV4In0.ElYpo9WdpgCbQsFCYrBsvg";

class MapComponent extends PureComponent {
  constructor(props) {
    super(props);
    this.state = {
      viewport: {
        latitude: 45.50884,
        longitude: -73.58781,
        zoom: 15,
      },
    };
    
    navigator.geolocation.getCurrentPosition(function (position) {
      var longitude = (featureLayer.getGeoJSON()).features[0].geometry.coordinates[0];
var latitude = (featureLayer.getGeoJSON()).features[0].geometry.coordinates[1];
      this.setState({
        viewport: {
          latitude: position.coords.latitude,
          longitude: position.coords.longitude,
          zoom: 15,
        },
      });
    });
  }

  render() {
    const { viewport } = this.state;
    return (
      <div>
        <ReactMapGL
          mapboxApiAccessToken={
            "pk.eyJ1IjoicHVza2k5NiIsImEiOiJja25hOTJzeTAwenRjMnJ0YXQwMzRpNWV4In0.ElYpo9WdpgCbQsFCYrBsvg"
          }
          mapStyle="mapbox://styles/mapbox/streets-v11"
          {...viewport}
          {...mapStyle}
          onViewportChange={(viewport) => this.setState({ viewport })}
        ></ReactMapGL>
      </div>
    );
  }
}

export default MapComponent;
