import React, { Component } from "react";
import Slider from "./Slider.js";
import FajtaComponent from "./FajtaComponent";
import { Carousel } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import img1 from "../img/isabel.jpg";
import img2 from "../img/hampshirek.png";

class FajtaleirasokComponent extends Component {
  constructor(props) {
    super(props);
    this.addActiveClass = this.addActiveClass.bind(this);
    this.state = {
      active: false,
    };
  }
  toggleClass() {
    const currentState = this.state.active;
    this.setState({ active: !currentState });
  }

  render() {
    const activat = this.state.activated ? "show" : "hide";
    const inactivat = this.state ? "hide" : " show";
    return (
      <div>
        <hr />
        <Carousel interval={2000}>
          <Carousel.Item>
            <img src={img1} height="600px" width="100%" alt="img1" />
            <div className="in-left">
              <h1>Udvozollek a fajtaleirasoknal</h1>
              <div>
                ide is fog jonni szoveg csak meg nem tudom hogy mit tegyek be,
                valamit ami fontos lehet
              </div>
            </div>
          </Carousel.Item>
          <Carousel.Item>
            <img src={img2} height="600px" width="100%" alt="img1" />
          </Carousel.Item>
        </Carousel>
        <div className="filter">
          <div className="filterBar">
            <div
              className="navbarFilter"
              style={{ justifyContent: "space-evenly" }}
            >
              <ul>
                <li onClick={() => this.changeSlide(0)}>
    className={this.state.activeIndex !== 0 ? 'column' : 'column active'}>
                  <a onClick={this.toggleClass}>Leiras</a>
                </li>
                <li className={"selectedhide"}>
                  <a onClick={this.toggleClass}>Nyulak</a>
                </li>
                <li className={"selectedhide"}>
                  <a>Tyukok</a>
                </li>
              </ul>
            </div>
          </div>
        </div>
        <FajtaComponent />
      </div>
    );
  }
}

export default FajtaleirasokComponent;
