import FajtaService from "../service/FajtaService.js";

class FajtaComponent extends Component {
  constructor(props) {
    super(props);

    this.state = {
      fajtak: [],
    };
  }

  componentDidMount() {
    FajtaService.getFajtak()
      .then((res) => {
        this.setState({ fajtak: res.data });
        console.log(this.state.data);
      })
      .catch(function (ex) {
        console.log("Response parsing failed. Error: ", ex);
      });
  }

  render() {
    function importAll(r) {
      return r.keys().map(r);
    }

    const images = importAll(require.context("./", false, /\.(png|jpeg|svg)$/));
    return (
      <div className="fajtaContainer">
        {this.state.fajtak.map((fajta) => (
          <article className="initialBox">
            <div>
              <img
                className="fajtaImg"
                src={"../img/fajtak/"+fajta.kep}
                alt=""
              />
            </div>
            <div className="leiras">
              <h2>{fajta.fajta}</h2>
              <div className="szoveg"></div>
              <button className="button">Olvass tobbet</button>
            </div>
          </article>
        ))}
        <article className="tartalom">
          <div className="initialBox">
            <div>
              <img className="fajtaImg" src={kep4} />
