import React, { Component, Route, Router } from "react";
import logo from "../img/logook.jpg";
import HirdetesekComponent from "./HirdetesekComponent";
import { Redirect } from "react-router-dom";

<Router>
  <Route path="/hirdetesek" component={HirdetesekComponent} />;
</Router>;
class Header extends Component {
  state = {
    redirect: false,
  };
  setRedirect = () => {
    this.setState({
      redirect: true,
    });
  };
  renderRedirect = () => {
    if (this.state.redirect) {
      return <Redirect to="/hirdetesek" />;
    }
  };
  render() {
    return (
      <div className="header-container">
        <div className="header-row">
          <div id="logo">
            <img src={logo} alt="logo" className="header-pic" />
          </div>

          <div id="menu">
            <nav id="navbar">
              <ul className="menu-items">
                <li>Kezdolap</li>
                <li className="scrollmenu">Fajtaleirasok</li>
                <li>
                  <a onClick={this.setRedirect}>
                    {this.renderRedirect()}
                    <button >Redirect</button>
                  </a>
                </li>
                <li>Rolunk</li>
                <li>Kapcsolat</li>
              </ul>
            </nav>
          </div>
          <div id="right-navbar">
            <i className="fa fa-bars" aria-hidden="true"></i>
            <i className="fa fa-user" aria-hidden="true" id="userIcon"></i>
          </div>
        </div>
      </div>
    );
  }
}

export default Header;
