import React, { Component } from "react";
import axios from "axios";
import FajtaService from "../service/FajtaService.js";

class StatisztikaComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      fajtak: [],
      counted: {},
    };
  }
  componentDidMount() {
    FajtaService.getFajtak()
      .then((res) => {
        this.setState({ fajtak: res.data });
        console.log(this.state.data);
      })
      .catch(function (ex) {
        console.log("Response parsing failed. Error: ", ex);
      });
    this.state.fajtak
      .forEach((elem) => {
        axios
          .get("http://localhost:8090/api/kisallat/counter", {
            params: {
              id: elem.id,
            },
          })
          .then((response) => {
            let count = {
              fajta: elem.id,
              number: response.data,
            };
            this.setState({ counted: count });
            console.log(this.state.counted);
          });
        this.setState({ loaded: true });
      })
      .catch(function (error) {
        console.log(error);
      });
    console.log(this.state.counted);
  }
  render() {
    return (
      <div>
        <table>
          <thead>
            <tr>
              <th>Rasa</th>
              <th>Numar</th>
            </tr>
          </thead>
          <tbody>
            {/* {this.state.counted.map((count) => (
              <tr key={count.fajta}>
                <td>{count.fajta}</td>
                <td>{count.number}</td>
              </tr>
            ))} */}
          </tbody>
        </table>
      </div>
    );
  }
}

export default StatisztikaComponent;
