import React, { Component } from "react";
import kep1 from "../img/album/20200204_113822.jpg";
import HirdetesService from "../service/HirdetesService";

class HirdetesComponent extends Component {
  constructor(props) {
    super(props);

    this.state = {
      hirdetesek: {},
      name: "",
      location: {},
    };
  }

  componentDidMount() {
    navigator.geolocation.getCurrentPosition(function (position) {
      console.log("Latitude is :", position.coords.latitude);
      console.log("Longitude is :", position.coords.longitude);
    });
    HirdetesService.getHirdetesek()
      .then((res) => {
        this.setState({ hirdetesek: res.data });
      })
      .catch(function (ex) {
        console.log("Response parsing failed. Error: ", ex);
      });
  }

  render() {
    // if (!this.props.google) {
    //   return <div>Loading...</div>;
    // }
    function importAll(r) {
      return r.keys().map(r);
    }

    const images = importAll(require.context("./", false, /\.(png|jpeg|svg)$/));
    const kepURL =
      "C/Users/En/Documents/workspace-spring-tool-suite-4-4.7.0.RELEASE/projekt/uploads/";
    return (
      <div className="containerAd">
        <div className="oneRow">
          <div className="row">
            <div className="leftContent">
              <div className="img">
                <img src={this.state.hirdetesek.kep.pic} alt="" />
              </div>
              <div className="details">
                <h3 className="leftCName">{this.state.hirdetesek.nev}</h3>
                <span className="info">{this.state.hirdetesek.leiras}</span>
                <br />
                <span className="info"> {this.state.hirdetesek.db} buc</span>
                <span className="info">{this.state.hirdetesek.ar} RON</span>
              </div>
            </div>
            <div className="rigthContent">
              <div className="contactInfo">
                <p>{this.state.hirdetesek.neve}</p>
                <p>{this.state.hirdetesek.cime}</p>
              </div>
              <div className="contact">
                <div className="message">
                  <a href={"mailto:" + this.state.hirdetesek.email}>Email</a>
                </div>
                <div className="telefon">
                  <a href={`tel:[${hirdetes.telefonszam}]`}>Telefon</a>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default HirdetesComponent;
