import React, { Component } from "react";
import kep1 from "../img/album/20200204_113822.jpg";
import HirdetesService from "../service/HirdetesService";
import { Map, GoogleApiWrapper, Marker } from "google-maps-react";

const mapStyles = {
  width: "100%",
  height: "60%",
};
class HirdetesComponent extends Component {
  constructor(props) {
    super(props);

    this.state = {
      hirdetesek: [],
      name: "",
      yourLong: "",
      yourLat: "",
    };
  }

  componentDidMount() {
    navigator.geolocation.getCurrentPosition(function (position) {
      this.setState({yourLong:position.coords.longitude})
      this.setState({})
      console.log(position);
    });
    HirdetesService.getHirdetesek()
      .then((res) => {
        this.setState({ hirdetesek: res.data });
      })
      .catch(function (ex) {
        console.log("Response parsing failed. Error: ", ex);
      });
  }

  render() {
    return (
      <div className="containerAd">
        {this.state.hirdetesek.map((hirdetes) => (
          <div className="oneRow">
            <div className="row">
              <div className="leftContent">
                <div className="img">
                  <img src={kep1} alt="" />
                </div>
                <div className="details">
                  <h3 className="leftCName">{hirdetes.nev}</h3>
                  <span className="info">{hirdetes.leiras}</span>
                  <br />
                  <span className="info"> {hirdetes.db} buc</span>
                  <span className="info">{hirdetes.ar} RON</span>
                </div>
              </div>
              <div className="rigthContent">
                <div className="contactInfo">
                  <p>{hirdetes.neve}</p>
                  <p>{hirdetes.cime}</p>
                </div>
                <div className="contact">
                  <div className="message">
                    <a href={"mailto:" + hirdetes.email}>Email</a>
                  </div>
                  <div className="telefon">
                    <a href={`tel:[${hirdetes.telefonszam}]`}>Telefon</a>
                  </div>
                </div>
              </div>
            </div>
          </div>
        ))}
        <div>
          <Map
            google={this.props.google}
            zoom={14}
            style={mapStyles}
            initialCenter={{
              lat: YOUR_LATITUDE,
              lng: YOUR_LONGITUDE,
            }}
          >
            <Marker onClick={this.onMarkerClick} name={"This is test name"} />
          </Map>
        </div>
      </div>
    );
  }
}

export default HirdetesComponent;
