import React, { Component } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

class RegisterComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      helysegek: [],
    };
  }
  componentDidMount() {
    axios
      .get(`http://localhost:8090/api/helysegek`)
      .then((res) => {
        this.setState({ helysegek: res.data });
        console.log(res.data);
      })
      .catch(function (ex) {
        console.log("Response parsing failed. Error: ", ex);
      });
  }

  render() {
    if (this.state.helysegek.length != 0)
      return (
        <div>
          <h2>Înregistrare</h2>
          <input className="reginput" placeholder="Numele și prenumele"></input>
          <input className="reginput" placeholder="Adresa"></input>
          <input className="reginput" placeholder="Nr telefon"></input>
          <select onChange={this.changeHelysegHandler}>
            {this.state.helysegek.map((option) => (
              <option value={option.helysegNev}>{option.helysegNev}</option>
            ))}
          </select>
          <input className="reginput" placeholder="Email"></input>
          <input type="password" className="reginput" placeholder="Password"></input>
          <input type="password" className="reginput" placeholder="Password"></input>
        </div>
      );
    return null;
  }
}

export default RegisterComponent;
